keyframes.css:
@layer utilities {
  .animate-in {
    animation: fadeIn 0.2s ease-out;
  }
  .animate-out {
    animation: fadeOut 0.2s ease-in;
  }
  .fade-in-0 {
    animation: fadeIn 0.2s ease-out;
  }
  .fade-out-0 {
    animation: fadeOut 0.2s ease-in;
  }
  .zoom-in-95 {
    animation: zoomIn95 0.2s ease-out;
  }
  .zoom-out-95 {
    animation: zoomOut95 0.2s ease-in;
  }
  .slide-in-from-left-1\/2 {
    animation: slideInFromLeft50 0.2s ease-out;
  }
  .slide-in-from-top-\[48\%\] {
    animation: slideInFromTop48 0.2s ease-out;
  }
  .slide-out-to-left-1\/2 {
    animation: slideOutToLeft50 0.2s ease-in;
  }
  .slide-out-to-top-\[48\%\] {
    animation: slideOutToTop48 0.2s ease-in;
  }
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes fadeOut {
  from {
    opacity: 1;
  }
  to {
    opacity: 0;
  }
}

@keyframes zoomIn95 {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes zoomOut95 {
  from {
    opacity: 1;
    transform: scale(1);
  }
  to {
    opacity: 0;
    transform: scale(0.95);
  }
}

@keyframes slideInFromLeft50 {
  from {
    transform: translateX(-50%);
  }
  to {
    transform: translateX(0);
  }
}

@keyframes slideInFromTop48 {
  from {
    transform: translateY(-48%);
  }
  to {
    transform: translateY(0);
  }
}

@keyframes slideOutToLeft50 {
  from {
    transform: translateX(0);
  }
  to {
    transform: translateX(-50%);
  }
}

@keyframes slideOutToTop48 {
  from {
    transform: translateY(0);
  }
  to {
    transform: translateY(-48%);
  }
}

@theme {
  --animate-in: fadeIn 0.2s ease-out;
  --animate-out: fadeOut 0.2s ease-in;
  --animate-fade-in: fadeIn 0.2s ease-out;
  --animate-fade-out: fadeOut 0.2s ease-in;
  --animate-zoom-in-95: zoomIn95 0.2s ease-out;
  --animate-zoom-out-95: zoomOut95 0.2s ease-in;
  --animate-slide-in-from-left-50: slideInFromLeft50 0.2s ease-out;
  --animate-slide-in-from-top-48: slideInFromTop48 0.2s ease-out;
  --animate-slide-out-to-left-50: slideOutToLeft50 0.2s ease-in;
  --animate-slide-out-to-top-48: slideOutToTop48 0.2s ease-in;

  @keyframes fadeIn {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }

  @keyframes fadeOut {
    from {
      opacity: 1;
    }
    to {
      opacity: 0;
    }
  }

  @keyframes zoomIn95 {
    from {
      opacity: 0;
      transform: scale(0.95);
    }
    to {
      opacity: 1;
      transform: scale(1);
    }
  }

  @keyframes zoomOut95 {
    from {
      opacity: 1;
      transform: scale(1);
    }
    to {
      opacity: 0;
      transform: scale(0.95);
    }
  }

  @keyframes slideInFromLeft50 {
    from {
      transform: translateX(-50%);
    }
    to {
      transform: translateX(0);
    }
  }

  @keyframes slideInFromTop48 {
    from {
      transform: translateY(-48%);
    }
    to {
      transform: translateY(0);
    }
  }

  @keyframes slideOutToLeft50 {
    from {
      transform: translateX(0);
    }
    to {
      transform: translateX(-50%);
    }
  }

  @keyframes slideOutToTop48 {
    from {
      transform: translateY(0);
    }
    to {
      transform: translateY(-48%);
    }
  }
}
