keyframes.css:
@layer utilities {
  .animate-ring {
    animation: ring 1.5s ease-in-out infinite;
  }
  .animate-intermittent {
    animation: intermittent 1s ease-in-out infinite;
  }
  .animate-wave {
    animation: wave 1.2s ease-in-out infinite;
  }
}

@keyframes ring {
  0% {
    stroke-dasharray: 1, 200;
    stroke-dashoffset: 0;
  }
  50% {
    stroke-dasharray: 90, 200;
    stroke-dashoffset: -35px;
  }
  100% {
    stroke-dasharray: 90, 200;
    stroke-dashoffset: -124px;
  }
}

@keyframes intermittent {
  0%,
  100% {
    transform: scale(0);
    animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
  }
  50% {
    transform: scale(1);
    animation-timing-function: cubic-bezier(0, 0, 0.2, 1);
  }
}

@keyframes wave {
  0%,
  40%,
  100% {
    transform: scaleY(0.4);
  }
  20% {
    transform: scaleY(1);
  }
}

@theme {
  --animate-ring: ring 1.5s ease-in-out infinite;
  --animate-intermittent: intermittent 1s ease-in-out infinite;
  --animate-wave: wave 1.2s ease-in-out infinite;

  @keyframes ring {
    0% {
      stroke-dasharray: 1, 200;
      stroke-dashoffset: 0;
    }
    50% {
      stroke-dasharray: 90, 200;
      stroke-dashoffset: -35px;
    }
    100% {
      stroke-dasharray: 90, 200;
      stroke-dashoffset: -124px;
    }
  }

  @keyframes intermittent {
    0%,
    100% {
      transform: scale(0);
      animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
    }
    50% {
      transform: scale(1);
      animation-timing-function: cubic-bezier(0, 0, 0.2, 1);
    }
  }

  @keyframes wave {
    0%,
    40%,
    100% {
      transform: scaleY(0.4);
    }
    20% {
      transform: scaleY(1);
    }
  }
}
